import React from 'react';

import HomeHeader from '../../components/HomeHeader';
import RecipeCard from '../../components/RecipeCard';
import Category from '../../components/Category';
import Footer from '../../components/Footer';

import { categories } from '../../demo/categories.json';
import { recipes } from '../../demo/recipes.json';
import { demoUsers } from '../../demo/users.json';

import './home.css';

const Home = () => {
  const cats = categories.map((c) => {
    return <Category category={c} key={c.id} />;
  });

  const recipe = recipes.map((r) => {
    return <RecipeCard recipe={r} key={r.id} />;
  });

  const users = demoUsers.map((u) => {
    return (
      <a href="#e" key={u.id} className="home-user-cnt">
        <img src="https://itmonitoring.pro/wp-content/plugins/all-in-one-seo-pack/images/default-user-image.png" alt="..." />
        <span>{ u.name }</span>
      </a>
    );
  });

  return (
    <>
      <HomeHeader />

      <div className="container">
        <section className="recipe-cards">
          <div className="home-line-block">
            <h2><span className="color-blue">Рецепты</span> для Вас</h2>
            <p>Мы сделали анализ Ваших действие и интересов и собрали для Вас несколько вкусных и полезных рецептов.</p>
          </div>

          <div className="row">
            { recipe }
          </div>

          <div className="text-center">
            <a href="#e" className="btn btn-info btn-lg btn-more">Больше рецептов</a>
          </div>
        </section>

        <section className="recipe-cards">
          <div className="home-line-block">
            <h2><span className="color-blue">ТОП</span> Недели</h2>
            <p>Лучшие 10 авторов за предыдущую неделю.</p>
          </div>

          <div className="row">
            <div className="col-8 home-users">
              { users }
            </div>
            <div className="col-4">
              <div className="card border-dark text-white mb-3">
                <div className="card-header"><h5 className="card-h-text">Стань автором!</h5></div>
                <div className="card-body text-dark">
                  <p className="card-text">
                    Хочешь стать автором? Регистрируйся на сайте, добавь рецепты, поделись опытом и стань популярным.
                  </p>
                  <a href="#a" className="btn btn-outline-dark mr-2">Войти</a>
                  <a href="#a" className="btn btn-outline-dark">Регистрация</a>
                </div>
              </div>
            </div>
          </div>

          <div className="text-center">
            <a href="#e" className="btn btn-info btn-lg btn-more">Все пользователи</a>
          </div>
        </section>

        <section className="home-banner">
          <div className="home-line-block">
            <h2>Категории <span className="color-blue">рецептов</span></h2>
            <p>Список популярных категории вкусных рецептов.</p>
          </div>
          <div className="row">
            { cats }
          </div>
        </section>
      </div>

      <Footer />
    </>
  );
};

export default Home;
