import React from 'react';
import PropTypes from 'prop-types';
import {Nav, Navbar, NavDropdown} from 'react-bootstrap';
import { FaUserAlt } from 'react-icons/fa';

const Menu = ({ type }) => {
  let addButtonClasses = 'btn btn-outline-success';

  const menuType = {
    bg: 'light',
    variant: 'light',
    className: 'justify-content-end light-top-menu'
  };

  if (type === 'home') {
    menuType.bg = 'transparent';
    menuType.variant = 'dark';
    menuType.className = 'justify-content-end transparent-top-menu';

    addButtonClasses = 'btn btn-outline-light';
  }

  return (
    <Navbar collapseOnSelect { ...menuType }>
      <Navbar.Brand href="#home">
        <img src="/logo.png" alt="Вкусные рецепты - Recept.fun" className="menu-logo" />
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav" className="justify-content-end">
        <Nav>
          <Nav.Link href="#features">Рецепты</Nav.Link>
          <Nav.Link href="#features">Пользователи</Nav.Link>
          <NavDropdown title="Категории" id="collasible-nav-dropdown">
            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
            <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
            <NavDropdown.Divider />
            <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
          </NavDropdown>
        </Nav>

        <button className="btn btn-link"><FaUserAlt /> Вход / Регистрация</button>
        <button className={ addButtonClasses }>Добавить рецепт</button>
      </Navbar.Collapse>
    </Navbar>
  );
};

Menu.propTypes = {
  type: PropTypes.string
};

export default Menu;
